hn.script:
function onValueChanged()
	if getValue() == 1 then
		window.skill_use.setVisible(true);
		window.skill_value.setVisible(true);							
		window.name.setAnchor("left", "", "", "", 25);
		window.name.setFrame ("");
	else
		window.name.setFrame ("headergray");
		window.name.setAnchor("left", "", "", "", 5);
	end
	window.name.changeStat(getValue());
end

function onInit()
	onValueChanged();
end

simplestring.script:
function changeStat(nStat)
	if nStat == 0 then
		setFrame ("headergray");
		setAnchor("left", "", "", "", 5);
	else
		setFrame ("");
	end
end
function onInit()
	changeStat(window.type.getValue());
end

number_skill_rollable.script:
function getStat(sFindStat)
	local sStat = "";
	local nStat = 0;

	for k, v in pairs(DataCommon.skilldata) do
		if k == sFindStat then
			sStat = v.stat;
			break;
		end
	end

	if sStat == "Body" then
		nStat = window.windowlist.window.body.getValue();
	elseif sStat == "Craft" then
		nStat = window.windowlist.window.crafting.getValue();
	elseif sStat == "Empathy" then
		nStat = window.windowlist.window.empathy.getValue();
	elseif sStat == "Intelligence" then
		nStat = window.windowlist.window.intelligence.getValue();
	elseif sStat == "Reflex" then
		nStat = window.windowlist.window.reflex.getValue();
	elseif sStat == "Will" then
		nStat = window.windowlist.window.will.getValue();
	elseif sStat == "Dexterity" then
		nStat = window.windowlist.window.dexterity.getValue();
	end
	return sStat,nStat;
end

function action(draginfo)
	local rActor = ActorManager.resolveActor(window.windowlist.window.getDatabaseNode());
	local sStat = "";
	local nStat = 0;

	sStat, nStat = getStat(window.name.getValue());

	ActionSkill.performRoll(draginfo, rActor, sStat.." "..window.name.getValue(), getValue() + nStat, "");
	return true;
end

function onDragStart(button, x, y, draginfo)
	return action(draginfo);
end

function onDoubleClick(x,y)
	return action();
end
