hs.script:
function getValStat(sStat)
	local nVal = 0;
	if sStat == "INT" then
		nVal = window.intelligence.getValue();
	elseif sStat == "REF" then
		nVal = window.reflex.getValue();
	elseif sStat == "DEX" then
		nVal = window.dexterity.getValue();
	elseif sStat == "BOD" then
		nVal = window.body.getValue();
	elseif sStat == "EMP" then
		nVal = window.empathy.getValue();
	elseif sStat == "CRA" then
		nVal = window.crafting.getValue();
	elseif sStat == "WIL" then
		nVal = window.will.getValue();
	end
	return nVal;
end

function onValueChanged()
	local sVal = getValue();
	local sSkill = "";
	local sAbility = "";
	local sStat = "";
	local nValStat = 0;

	sSkill = ProfessionTable.tbl_profession[sVal]["root"]["skill"];
	window.root_skills_st.setValue(sSkill);
	sStat = ProfessionTable.tbl_profession[sVal]["root"]["stat"];
	window.root_skills_bs.setValue(sStat);
	nValStat = getValStat(sStat);
	window.root_skills_nb.setValue(nValStat);

	sAbility = ProfessionTable.tbl_profession[sVal]["ability"]["branch1"];
	window.a1_label_ss.setValue(sAbility);

	sSkill = ProfessionTable.tbl_profession[sVal][sAbility]["skill1"];
	window.a1_ability_st.setValue(sSkill);
	sStat = ProfessionTable.tbl_profession[sVal][sAbility]["stat1"];
	window.a1_ability_bs.setValue(sStat);
	nValStat = getValStat(sStat);
	window.a1_ability_nb.setValue(nValStat);
	sSkill = ProfessionTable.tbl_profession[sVal][sAbility]["skill2"];
	window.a2_ability_st.setValue(sSkill);
	sStat = ProfessionTable.tbl_profession[sVal][sAbility]["stat2"];
	window.a2_ability_bs.setValue(sStat);
	nValStat = getValStat(sStat);
	window.a2_ability_nb.setValue(nValStat);
	sSkill = ProfessionTable.tbl_profession[sVal][sAbility]["skill3"];
	window.a3_ability_st.setValue(sSkill);
	sStat = ProfessionTable.tbl_profession[sVal][sAbility]["stat3"];
	window.a3_ability_bs.setValue(sStat);
	nValStat = getValStat(sStat);
	window.a3_ability_nb.setValue(nValStat);

	sAbility = ProfessionTable.tbl_profession[sVal]["ability"]["branch2"];
	window.a2_label_ss.setValue(sAbility);

	sSkill = ProfessionTable.tbl_profession[sVal][sAbility]["skill1"];
	window.b1_ability_st.setValue(sSkill);
	sStat = ProfessionTable.tbl_profession[sVal][sAbility]["stat1"];
	window.b1_ability_bs.setValue(sStat);
	nValStat = getValStat(sStat);
	window.b1_ability_nb.setValue(nValStat);
	sSkill = ProfessionTable.tbl_profession[sVal][sAbility]["skill2"];
	window.b2_ability_st.setValue(sSkill);
	sStat = ProfessionTable.tbl_profession[sVal][sAbility]["stat2"];
	window.b2_ability_bs.setValue(sStat);
	nValStat = getValStat(sStat);
	window.b2_ability_nb.setValue(nValStat);
	sSkill = ProfessionTable.tbl_profession[sVal][sAbility]["skill3"];
	window.b3_ability_st.setValue(sSkill);
	sStat = ProfessionTable.tbl_profession[sVal][sAbility]["stat3"];
	window.b3_ability_bs.setValue(sStat);
	nValStat = getValStat(sStat);
	window.b3_ability_nb.setValue(nValStat);

	sAbility = ProfessionTable.tbl_profession[sVal]["ability"]["branch3"];
	window.a3_label_ss.setValue(sAbility);

	sSkill = ProfessionTable.tbl_profession[sVal][sAbility]["skill1"];
	window.c1_ability_st.setValue(sSkill);
	sStat = ProfessionTable.tbl_profession[sVal][sAbility]["stat1"];
	window.c1_ability_bs.setValue(sStat);
	nValStat = getValStat(sStat);
	window.c1_ability_nb.setValue(nValStat);
	sSkill = ProfessionTable.tbl_profession[sVal][sAbility]["skill2"];
	window.c2_ability_st.setValue(sSkill);
	sStat = ProfessionTable.tbl_profession[sVal][sAbility]["stat2"];
	window.c2_ability_bs.setValue(sStat);
	nValStat = getValStat(sStat);
	window.c2_ability_nb.setValue(nValStat);
	sSkill = ProfessionTable.tbl_profession[sVal][sAbility]["skill3"];
	window.c3_ability_st.setValue(sSkill);
	sStat = ProfessionTable.tbl_profession[sVal][sAbility]["stat3"];
	window.c3_ability_bs.setValue(sStat);
	nValStat = getValStat(sStat);
	window.c3_ability_nb.setValue(nValStat);
end

function onInit()
	if window.profession_initialisation.getValue() == 0 then
		onValueChanged();
		window.profession_initialisation.setValue(1);
	end
end

button_checkbox.script:
function onInit()
	if window.type.getValue() == 1 then
		setVisible(true);
	end
end

simplestring.script:
function onInit()
	if window.type.getValue() == 0 then
		setFrame ("headergray");
		setAnchor("left", "", "", "", 5);
	else
		setFrame ("");
	end
end

basicnumber.script:
function onInit()
	if window.type.getValue() == 1 then
		setVisible(true);
	end
end
