windowclass.script:
function onDrop(x, y, draginfo)
	local sDragType = draginfo.getType();

	if sDragType == "dice" then
		local w = list.addEntry(true);
		for _, vDie in ipairs(draginfo.getDieList()) do
			w.dice.addDie(vDie.type);
		end
		return true;
	elseif sDragType == "number" then
		local w = list.addEntry(true);
		w.bonus.setValue(draginfo.getNumberData());
		return true;
	end
end

button_checkbox.script:
function onInit()
	local visible = (window.weffect_bleeding_use.getValue() == 1)
	window.bleeding_amount.setVisible(visible);
	window.label_bleedingpercent.setVisible(visible);
end
function onClickRelease(button, x, y)
	local visible = (window.weffect_bleeding_use.getValue() == 0)
	window.bleeding_amount.setVisible(visible);
	window.label_bleedingpercent.setVisible(visible);
end

function onInit()
	local visible = (window.weffect_focus_use.getValue() == 1)
	window.focus_amount.setVisible(visible);
end
function onClickRelease(button, x, y)
	local visible = (window.weffect_focus_use.getValue() == 0)
	window.focus_amount.setVisible(visible);
end

function onInit()
	local visible = (window.weffect_stun_use.getValue() == 1)
	window.stun_amount.setVisible(visible);
end
function onClickRelease(button, x, y)
	local visible = (window.weffect_stun_use.getValue() == 0)
	window.stun_amount.setVisible(visible);
end

list_editor.script:
function addEntry(bFocus)
	local n = getWindowCount();

	local w = createWindow();
	if w then
		if bFocus then
			w.type.setFocus();
		end
	end

	return w;
end

enhancement_list_editor.script:
function addEntry(bFocus)
	local n = getWindowCount();
	if n < window.enhancementmax.getValue() then
		local w = createWindow();
		if w then
			if bFocus then
				w.name.setFocus();
			end
		end

		return w;
	end
end

