combobox.script:
function onInit()
	super.onInit();
	add(Interface.getString("weapon_rune_chemobog_label"), Interface.getString("weapon_rune_chemobog_label"));
	add(Interface.getString("weapon_rune_dazhbog_label"), Interface.getString("weapon_rune_dazhbog_label"));
	add(Interface.getString("weapon_rune_devanna_label"), Interface.getString("weapon_rune_devanna_label"));
	add(Interface.getString("weapon_rune_morana_label"), Interface.getString("weapon_rune_morana_label"));
	add(Interface.getString("weapon_rune_perun_label"), Interface.getString("weapon_rune_perun_label"));
	add(Interface.getString("weapon_rune_stribog_label"), Interface.getString("weapon_rune_stribog_label"));
	add(Interface.getString("weapon_rune_svarog_label"), Interface.getString("weapon_rune_svarog_label"));
	add(Interface.getString("weapon_rune_triglav_label"), Interface.getString("weapon_rune_triglav_label"));
	add(Interface.getString("weapon_rune_veles_label"), Interface.getString("weapon_rune_veles_label"));
	add(Interface.getString("weapon_rune_zoria_label"), Interface.getString("weapon_rune_zoria_label"));
end
function onValueChanged()
	window.effect.setValue(Interface.getString("weapon_rune_" .. string.lower(getValue()) .. "_desc"));
end
